// R√©cup√©rer tous les items de l'agr√©gation
const allItems = $input.all();

// Extraire toutes les extensions avec leurs IDs
const extractExtensions = () => {
  const extensions = [];

  for (const item of allItems) {
    const data = item.json.data;

    // Ignorer le header (premi√®re ligne)
    if (Array.isArray(data) && data.length > 1) {
      for (let i = 1; i < data.length; i++) {
        const row = data[i];

        // Chercher les colonnes Extension et potentiellement ID_3CX
        // Les noms de colonnes peuvent varier : __EMPTY_1, Extension, etc.
        let extension = null;
        let id3cx = null;

        // Essayer diff√©rentes cl√©s possibles pour l'extension
        if (row.__EMPTY_1) extension = row.__EMPTY_1;
        else if (row.Extension) extension = row.Extension;
        else if (row.extension) extension = row.extension;

        // Si pas d'extension, ignorer cette ligne
        if (!extension || extension === '' || String(extension).startsWith('*')) {
          continue;
        }

        // Chercher l'ID 3CX (si disponible)
        if (row.ID_3CX) id3cx = row.ID_3CX;
        else if (row.id_3cx) id3cx = row.id_3cx;

        // Si pas d'ID 3CX, utiliser le num√©ro d'extension
        if (!id3cx) id3cx = extension;

        extensions.push({
          numero: String(extension).trim(),
          id3cx: String(id3cx).trim()
        });
      }
    }
  }

  return extensions;
};

// Extraire les extensions
const allExtensions = extractExtensions();

// D√©dupliquer par num√©ro d'extension
const uniqueExtensions = [];
const seen = new Set();

for (const ext of allExtensions) {
  if (!seen.has(ext.numero)) {
    seen.add(ext.numero);
    uniqueExtensions.push(ext);
  }
}

// Trier par num√©ro (ordre croissant)
uniqueExtensions.sort((a, b) => {
  const numA = parseInt(a.numero) || 0;
  const numB = parseInt(b.numero) || 0;
  return numA - numB;
});

// Limiter √† 18 BLF maximum
const blfExtensions = uniqueExtensions.slice(0, 18);

