// Fonction de parsing CSV robuste (g√®re les guillemets et d√©calages)
function parseCSV(data) {
  const lines = data.split('\n').filter(line => {
    const trimmed = line.trim();
    // Filtrer les lignes vides et les commentaires
    return trimmed && !trimmed.startsWith('#') && !trimmed.startsWith('//');
  });

  if (lines.length === 0) return [];

  // Parser les headers
  const headers = [];
  let current = '';
  let insideQuotes = false;
  const headerLine = lines[0];

  for (let i = 0; i < headerLine.length; i++) {
    const char = headerLine[i];
    const nextChar = headerLine[i + 1];

    if (char === '"') {
      if (insideQuotes && nextChar === '"') {
        current += '"';
        i++;
      } else {
        insideQuotes = !insideQuotes;
      }
    } else if (char === ',' && !insideQuotes) {
      headers.push(current.trim());
      current = '';
    } else {
      current += char;
    }
  }
  headers.push(current.trim());

  const expectedColumns = headers.length;
  console.log(`CSV Headers: ${expectedColumns} colonnes attendues`);

  // Parser les lignes de donn√©es
  const rows = [];
  for (let lineIdx = 1; lineIdx < lines.length; lineIdx++) {
    const line = lines[lineIdx];
    const row = [];
    current = '';
    insideQuotes = false;

    for (let charIndex = 0; charIndex < line.length; charIndex++) {
      const char = line[charIndex];
      const nextChar = line[charIndex + 1];

      if (char === '"') {
        if (insideQuotes && nextChar === '"') {
          current += '"';
          charIndex++;
        } else {
          insideQuotes = !insideQuotes;
        }
      } else if (char === ',' && !insideQuotes) {
        row.push(current.trim());
        current = '';
      } else {
        current += char;
      }
    }
    row.push(current.trim());

    // V√©rifier que le nombre de colonnes correspond
    if (row.length !== expectedColumns) {
      console.warn(`Ligne ${lineIdx + 1}: ${row.length} colonnes au lieu de ${expectedColumns} - IGNOR√âE`);
      console.warn(`D√©but de ligne: ${line.substring(0, 100)}`);
      continue; // Ignorer les lignes mal format√©es
    }

    rows.push(row);
  }

  // Convertir en objets
  return rows.map(cols => {
    const obj = {};
    headers.forEach((header, idx) => {
      obj[header] = cols[idx] || '';
    });
    return obj;
  });
}

const parsed = parseCSV(csvContent);

